import { useEffect, useRef } from 'react'
import Admin from './Admin/Admin'
import Company from './Company/Company'
import Customer from './Customer/Customer'
import './user.css'

export default function User(props) {

    const userRef = useRef()

    useEffect(() => {
        props.setSidebarVisible(true)
        props.setHeaderVisible(true)
        switch (props.sendToContent) {
            case 'Home':
                userRef.current.id = ''
                break;
            case 'Test':

                break;
            default:
                userRef.current.id = 'User-opacity'
                break;
        }
    })

    const getUser = () => {
        switch (props.content) {
            case 'Customer':
                return <Customer parentProps={props} />
            case 'Company':
                return <Company parentProps={props} />
            case 'Admin':
                return <Admin parentProps={props} />
            default:
                window.location.reload()
                break;
        }
    }

    return (<div className='User' ref={userRef}>
        <div User-user>
            {getUser()}
        </div>
    </div>)
}
