import './companyCouponActionbar.css'

export default function CompanyCouponActionbar() {

    // Get the modal
    var modal = document.getElementById("myModal");
    // Get the button that opens the modal
    var btn = document.getElementById("myBtn");
    // Get the <span> element that closes the modal
    var span = document.getElementsByClassName("close")[0];
    // When the user clicks on the button, open the modal
    btn.onclick = function () {
        modal.style.display = "block";
    }
    // When the user clicks on <span> (x), close the modal
    span.onclick = function () {
        modal.style.display = "none";
    }
    // When the user clicks anywhere outside of the modal, close it
    window.onclick = function (event) {
        if (event.target === modal) {
            modal.style.display = "none";
        }
    }

    return (
        <div className='CompanyCouponActionbar'>

            <div id="myModal" class="modal">

                <div class="modal-content">
                    <span class="close">&times;</span>
                    <p>Some text in the Modal..</p>
                </div>

            </div>

            <div className='action-sidebar'>
                <button className='action-sidebar-button'>Search Category</button>
                <div className='action-sidebar-button-seperator'></div>
                <button className='action-sidebar-button'>Search Price</button>
                <div className='action-sidebar-button-seperator'></div>
                <button className='action-sidebar-button' id="myBtn">Add Coupon</button>
                <div className='action-sidebar-button-seperator'></div>
                <button className='action-sidebar-button'>Update Coupon</button>
                <div className='action-sidebar-button-seperator'></div>
                <button className='action-sidebar-button'>Delete Coupons</button>
            </div>
        </div>
    )
}
