import { useEffect, useRef, useState } from 'react'
import { Route, Switch } from 'react-router-dom'
import Admin from './Admin/Admin'
import Company from './Company/Company'
import Customer from './Customer/Customer'
import './user.css'

export default function User() {

    const userRef = useRef()
    // const [style, setStyle] = useState()

    // useEffect(() => {

    //     switch (props.sendToContent) {
    //         case 'Home':
    //             userRef.current.id = ''
    //             break;
    //         default:
    //             userRef.current.id = 'User-opacity'
    //             break;
    //     }
    // })

    const setStyle = () => {

        < Switch >
            <Route path='/customer/home'>
                {console.log(userRef.current.id)}
                {/* {userRef.current.id = ''} */}
            </Route>
            <Route path='/customer/temp'>
                {/* {userRef.current.id = 'User-opacity'} */}
            </Route>
        </Switch >

    }

    // useEffect(() => {
    //     switch (style) {
    //         case 'Home':
    //             userRef.current.id = ''
    //             break;
    //         default:
    //             userRef.current.id = 'User-opacity'
    //             break;
    //     }
    // })

    const getUser = () => {
        setStyle()
        return (<Switch>
            <Route path='/customer'>
                <Customer setStyle={setStyle} />
            </Route>
            <Route path='/company'>
                <Company setStyle={setStyle} />
            </Route>
            <Route path='/admin'>
                <Admin setStyle={setStyle} />
            </Route>
        </Switch>)
    }

    return (<div className='User' ref={userRef} id=''>
        <div User-user>
            {getUser()}
        </div>

    </div>)
}
