import { useState } from 'react';
import './app.css';
import Footer from './Footer/Footer';
import Login from './content/Login/Login';
import Header from './Header/Header';
import Sidebar from './Sidebar/Sidebar';
import User from './content/User/User';
import { BrowserRouter as Router, Link } from 'react-router-dom'



function App() {
  const [content, setContent] = useState('Login')
  const [[headerVisible, setHeaderVisible], [sidebarVisible, setSidebarVisible]] = [useState(false), useState(false)]
  const [sendToContent, setSendToContent] = useState('Home')

  const getContent = () => {
    switch (content) {
      case 'Login':
        return <Login
          content={content}
          setContent={setContent}
          setSidebarVisible={setSidebarVisible}
          setHeaderVisible={setHeaderVisible}
        />
      case 'Customer':
      case 'Company':
      case 'Admin':
        return <User
          content={content}
          setContent={setContent}
          setSidebarVisible={setSidebarVisible}
          setHeaderVisible={setHeaderVisible}
          sendToContent={sendToContent}
        />
      default:
        window.location.reload()
        break;
    }
  }

  // seperate to 3 sidebars
  const getSidebar = () => {
    if (sidebarVisible)
      return <Sidebar setSendToContent={setSendToContent} content={content} />
  }
  const getHeader = () => {
    if (headerVisible)
      return <Header setSendToContent={setSendToContent} />
  }

  return (
    <Router>
      <div className="App">

        <Link to='/hello'>Home</Link>

        <div className="App-Header">
          {getHeader()}
        </div>
        <div className="App-Sidebar">
          {getSidebar()}
        </div>
        <div className="App-Content">
          {getContent()}
        </div>
        <div className="App-Footer">
          <Footer currentContent={content} />
        </div>
      </div>
    </Router >
  );
}

export default App;
