import { useState } from 'react';
import './app.css';
import Footer from './Footer/Footer';
import Login from './content/Login/Login';
import Header from './Header/Header';
import CustomerSidebar from './user-sidebar/CustomerSidebar/CustomerSidebar';
import CompanySidebar from './user-sidebar/CompanySidebar/CompanySidebar';
import AdminSidebar from './user-sidebar/AdminSidebar/AdminSidebar';
import User from './content/User/User';
import { BrowserRouter as Router, Link, Route, Switch } from 'react-router-dom'
import { Button } from 'semantic-ui-react'
import Temp from './Temp';



function App() {
  const [content, setContent] = useState('Login')
  const [[headerVisible, setHeaderVisible], [sidebarVisible, setSidebarVisible]] = [useState(false), useState(false)]
  const [sendToContent, setSendToContent] = useState('Home')
  // const [token, setToken] = useState('') use later for tokens

  // const getContent = () => {
  //   switch (content) {
  //     case 'Login':
  //       return <Login
  //         content={content}
  //         setContent={setContent}
  //         setSidebarVisible={setSidebarVisible}
  //         setHeaderVisible={setHeaderVisible}
  //       />
  //     case 'Customer':
  //     case 'Company':
  //     case 'Admin':
  //       return <User
  //         content={content}
  //         setContent={setContent}
  //         setSidebarVisible={setSidebarVisible}
  //         setHeaderVisible={setHeaderVisible}
  //         sendToContent={sendToContent}
  //       />
  //     default:
  //       window.location.reload()
  //       break;
  //   }
  // }

  const getContent = () => {
    <Switch>
      <Route path='/login'>
        <Login
          content={content}
          setContent={setContent}
          setSidebarVisible={setSidebarVisible}
          setHeaderVisible={setHeaderVisible}
        />
      </Route>
    </Switch>
  }

  const getSidebar = () => {
    switch (content) {
      case 'Customer':
        return <CustomerSidebar setSendToContent={setSendToContent} content={content} />
      case 'Company':
        return <CompanySidebar setSendToContent={setSendToContent} content={content} />
      case 'Admin':
        return <AdminSidebar setSendToContent={setSendToContent} content={content} />
      default:
        return null
    }
  }
  const getHeader = () => {
    if (headerVisible)
      return <Header setSendToContent={setSendToContent} />
  }

  //wrapping Link in button
  //   <Link>
  //   <Button to='/hello' className='form-control'>Home</Button>
  // </Link>

  return (
    <Router>
      <div className="App">
        <div className="App-Header">
          {getHeader()}
        </div>
        <div className="App-Sidebar">
          {getSidebar()}
        </div>
        <div className="App-Content">
          {getContent()}
          {/* <Route path='/login'>
            <Login
              content={content}
              setContent={setContent}
              setSidebarVisible={setSidebarVisible}
              setHeaderVisible={setHeaderVisible}
            />
          </Route> */}

          <Temp />

        </div>
        <div className="App-Footer">
          <Footer currentContent={content} />
        </div>
      </div>
    </Router >
  );
}

export default App;
