import { useEffect, useRef } from 'react'
import './company.css'
import './../../../../styles/user.css'

export default function Company(props) {

    const companyRef = useReff()

    useEffect(() => {
        props.setSidebarVisible(true)
        props.setHeaderVisible(true)
        switch (props.respondContent) {
            case 'Home':
                companyRef.current.id = ''
                break;
            case 'Test':
                companyRef.current.id = 'Customer-opacity'
                break;
            default:
                break;
        }
    })


    return (<div className='Company' ref={companyRef}>

    </div>)
}
