import './app.css';
import Footer from './Footer/Footer';
import Login from './content/Login/Login';
import Header from './Header/Header';
import CustomerSidebar from './user-sidebar/CustomerSidebar/CustomerSidebar';
import CompanySidebar from './user-sidebar/CompanySidebar/CompanySidebar';
import User from './content/User/User';
import { Route, Switch, Router, Redirect } from 'react-router-dom'
import history from './history';
import { Suspense, useState, lazy } from 'react';
import './../../styles/loading-div.css'
const AdminSidebar = lazy(() => import('./user-sidebar/AdminSidebar/AdminSidebar'))

function App() {
  const [token, setToken] = useState(null)

  const getContent = () => {
    return (
      <Switch>
        <Route path={["/customer", "/company", "/admin"]}>
          <User token={token} />
        </Route>
        <Route>
          <Redirect to='/login' setToken={setToken} />
          <Login />
        </Route>
      </Switch>
    )
  }

  const getSidebar = () => {
    return (
      <Switch>
        <Route path='/customer'>
          <CustomerSidebar />
        </Route>
        <Route path='/company'>
          <CompanySidebar />
        </Route>
        <Suspense fallback={<div className='loading-div'></div>}>
          <Route path='/admin'>
            <AdminSidebar />
          </Route>
        </Suspense>
      </Switch>
    )
  }
  const getHeader = () => {
    return (
      <Route path={["/customer", "/company", "/admin"]}>
        <Header setToken={setToken} />
      </Route>
    )
  }

  return (
    <Router history={history}>
      <div className="App">

        {getHeader()}
        <div className="row">
          <div className='col-md-1'></div>
          {getSidebar()}
          <div className='col-md-10'></div>
          {getContent()}
        </div>
        <Footer />
      </div>
    </Router >
  );
}

export default App;
