import { useEffect, useRef } from 'react'
import { Route, Switch } from 'react-router-dom'
import Admin from './Admin/Admin'
import Company from './Company/Company'
import Customer from './Customer/Customer'
import './user.css'

export default function User() {

    const userRef = useRef()

    // useEffect(() => {

    //     switch (props.sendToContent) {
    //         case 'Home':
    //             userRef.current.id = ''
    //             break;
    //         default:
    //             userRef.current.id = 'User-opacity'
    //             break;
    //     }
    // })

    const setStyle = () => {
        console.log(1324);
        < Switch >
            <Route path='/customer/home'>
                userRef.current.id = ''
            </Route>
            <Route path='/customer/temp'>
                [userRef.current.id = 'User-opacity']
            </Route>
        </Switch >
    }

    const getUser = () => {
        return (<Switch>
            <Route path='/customer'>
                <Customer />
            </Route>
            <Route path='/company'>
                <Company />
            </Route>
            <Route path='/admin'>
                <Admin />
            </Route>
        </Switch>)
    }

    return (<div className='User' ref={userRef}>
        <div User-user>
            {getUser()}
        </div>
        {setStyle()}
    </div>)
}
