import './company-update-coupon.css'
import history from '../../../../../../history'
import { Formik, Form, Field } from 'formik'

export default function CompanyUpdateCoupon() {
    const closeDiv = () => {
        if (window.confirm('Are you sure you want to close this window?'))
            history.push('/company/my-coupons')
    }

    const handleSubmit = (values) => {
        //TODO manage coupon submit here
        //TODO change date format if needed
        console.log(values)//TODO delete

        history.push('/company/my-coupons')
    }

    return (
        <>
            <div className='CompanyAddCoupon-background'></div>
            <div className="CompanyUpdateCoupon">
                <div className='CompanyAddCoupon-close-div'>
                    <button className="btn-close" aria-label="Close" id='CompanyAddCoupon-close-button' onClick={closeDiv}></button>
                </div>
                <h5 className='CompanyAddCoupon-title'>Update An Existing Coupon</h5>
            </div>
            <Formik
                initialValues={{
                    amount: '',
                    category: '',
                    description: '',
                    end_date: '',
                    image: '',
                    price: '',
                    start_date: getToday(),
                    title: ''
                }}
                onSubmit={handleSubmit}
            >
                {({ values }) => (
                    <Form>



                    </Form>
                )}
            </Formik >

        </>
    )
}
