import { useEffect, useRef, useState } from 'react'
import { Route, Switch } from 'react-router-dom'
import Admin from './Admin/Admin'
import Company from './Company/Company'
import Customer from './Customer/Customer'
import './user.css'

export default function User() {

    const userRef = useRef()
    const [style, setStyle] = useState('Home')

    useEffect(() => {
        switch (style) {
            case 'Home':
                userRef.current.id = ''
                break;
            default:
                userRef.current.id = 'User-opacity'
                break;
        }
    })

    const getUser = () => {

        return (<Switch>
            <Route path='/customer'>
                <Customer setStyle={setStyle} />
            </Route>
            <Route path='/company'>
                <Company setStyle={setStyle} />
            </Route>
            <Route path='/admin'>
                <Admin setStyle={setStyle} />
            </Route>
        </Switch>)
    }

    return (<div className='User' ref={userRef}>
        <div User-user>
            {getUser()}
        </div>
    </div>)


}
