import { useEffect, useRef } from "react"
import image from '../../../../assests/The Coupon Project Logo.png'
import '../sidebar.css'
import history from "../../history"
import { withRouter } from 'react-router-dom'

function CustomerSidebar() {
    const buttons = { home: useRef(), myCoupons: useRef(), coupons: useRef() }
    const buttonDivs = { home: useRef(), myCoupons: useRef(), coupons: useRef() }

    useEffect(() => {
        for (let button of Object.values(buttons)) {
            button.current.className = 'Sidebar-button'
        }
        for (let div of Object.values(buttonDivs)) {
            div.current.className = 'Sidebar-button-div'
        }
        switch (history.location.pathname) {
            case '/customer/home':
                buttonDivs.home.current.className = 'Sidebar-button-div-clicked'
                buttons.home.current.className = 'Sidebar-button-clicked'
                break;
            case '/customer/my-coupons':
                buttonDivs.test.current.className = 'Sidebar-button-div-clicked'
                buttons.test.current.className = 'Sidebar-button-clicked'
                break;
            case '/customer/coupons':
                buttonDivs.test.current.className = 'Sidebar-button-div-clicked'
                buttons.test.current.className = 'Sidebar-button-clicked'
                break;
            default:
                break;
        }
    })

    const buttonClick = (event) => {
        switch (event.target.name) {
            case 'My-Coupons':
                history.push('/customer/my-coupons')
                break;
            case 'Coupons':
                history.push('/customer/coupons')
                break;
            default:
                history.push('/customer/home')
                break;
        }
    }

    const imageClick = () => {
        history.push('/customer/home')
    }

    return (<div className='CustomerSidebar'>
        <img src={image} alt='logo' width='144' className='Sidebar-img' onClick={() => imageClick()} />
        <div className='Sidebar-img-div' onClick={() => imageClick()}></div>

        <div className='Siderbar-div-seperator'></div>
        <div className='Sidebar-button-div-clicked' ref={buttonDivs.home}>
            <button className='Sidebar-button-clicked' name='Home' ref={buttons.home} onClick={buttonClick} >Home Page</button>
        </div>

        <div className='Siderbar-div-button-seperator'></div>
        <div className='Sidebar-button-div' ref={buttonDivs.myCoupons}>
            <button className='Sidebar-button' name='My-Coupons' ref={buttons.myCoupons} onClick={buttonClick}>My Coupons</button>
        </div>

        <div className='Siderbar-div-button-seperator'></div>
        <div className='Sidebar-button-div' ref={buttonDivs.coupons}>
            <button className='Sidebar-button' name='Coupons' ref={buttons.coupons} onClick={buttonClick}>Coupons</button>
        </div>
    </div>)
}

export default withRouter(CustomerSidebar)
