import './app.css';
import Footer from './Footer/Footer';
import Login from './content/Login/Login';
import Header from './Header/Header';
import User from './content/User/User';
import { Route, Switch, Router, Redirect } from 'react-router-dom'
import history from './history';
import { Suspense, useState, lazy, useEffect } from 'react';
import './../../styles/loading-div.css'
const AdminSidebar = lazy(() => import('./user-sidebar/AdminSidebar/AdminSidebar'))
const CompanySidebar = lazy(() => import('./user-sidebar/CompanySidebar/CompanySidebar'))
const CustomerSidebar = lazy(() => import('./user-sidebar/CustomerSidebar/CustomerSidebar'))

function App() {
  const [token, setToken] = useState(null)

  //XXX refresh page
  // history.go(0)


  const getContent = () => {
    return (
      <Switch>
        <Route path={["/customer", "/company", "/admin"]}>
          <User token={token} />
        </Route>
        <Route>
          <Redirect to='/login' setToken={setToken} />
          <Login />
        </Route>
      </Switch>
    )
  }

  const getSidebar = () => {
    return (
      <Suspense fallback={<div className='loading-div'></div>}>
        <Switch>
          <Route path='/customer'>
            <CustomerSidebar />
          </Route>
          <Route path='/company'>
            <CompanySidebar />
          </Route>
          <Route path='/admin'>
            <AdminSidebar />
          </Route>
        </Switch>
      </Suspense>
    )
  }
  const getHeader = () => {
    return (
      <Route path={["/customer", "/company", "/admin"]}>
        <Header setToken={setToken} />
      </Route>
    )
  }

  const [intervalId, setIntervalId] = useState('')
  let number = 0

  useEffect(() => {
    return () => {
      clearInterval(intervalId)
    };
  }, [intervalId])

  useEffect(() => {
    setIntervalId(
      setInterval(() => {
        number++;
        console.log(number)
      }, 1000)
    )
    // eslint-disable-next-line
  })

  return (
    <Router history={history}>
      <div className="App">
        {getHeader()}
        {getSidebar()}
        {getContent()}
        <Footer />
      </div>
    </Router >
  );
}

export default App;
